import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminProductForm.css";

const ProductDashboard = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [createdProductId, setCreatedProductId] = useState("");

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    stock: "",
    brand: "",
    image: "",
    featured: false,
    warrantyPeriod: "",
  });

  const categories = [
    "Electronics",
    "Clothing",
    "Home & Garden",
    "Books",
    "Toys",
    "Sports",
    "Health & Beauty",
  ];

  useEffect(() => {
    const verifyAdminStatus = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("Please login to access this page");
        window.location.href = "/login";
        return;
      }

      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/profile",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (!data.isAdmin) {
          // Show alert and allow user to proceed if they click OK
          const confirmAccess = window.confirm(
            "You are not an admin. Are you sure you want to continue?"
          );

          if (confirmAccess) {
            setIsAdmin(true); // Grant access manually
          } else {
            window.location.href = "/"; // Cancel and go back
          }
        } else {
          setIsAdmin(true); // Normal admin access
        }
      } catch (err) {
        const errorMessage =
          err.response?.data?.message || "Failed to verify admin status";
        setError(errorMessage);
        console.error("Admin verification error:", err);
        window.location.href = "/login";
      } finally {
        setLoading(false);
      }
    };

    verifyAdminStatus();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    let processedValue = value;
    if (["price", "stock", "warrantyPeriod"].includes(name)) {
      processedValue = value.replace(/[^0-9.]/g, "");
    }

    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : processedValue,
    });
  };

  const validateForm = () => {
    const requiredFields = [
      "name",
      "description",
      "price",
      "category",
      "brand",
    ];
    const missingFields = requiredFields.filter(
      (field) => !formData[field]?.toString().trim()
    );

    if (missingFields.length > 0) {
      setError(`Missing required fields: ${missingFields.join(", ")}`);
      return false;
    }

    if (isNaN(formData.price) || Number(formData.price) <= 0) {
      setError("Price must be a positive number");
      return false;
    }

    if (formData.stock && (isNaN(formData.stock) || formData.stock < 0)) {
      setError("Stock must be a positive number");
      return false;
    }

    return true;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setCreatedProductId("");

    if (!validateForm()) return;
    setSubmitting(true);

    try {
      const token = localStorage.getItem("token");

      const payload = {
        name: formData.name.trim(),
        description: formData.description.trim(),
        price: Number(formData.price),
        category: formData.category.trim(),
        stock: formData.stock ? Number(formData.stock) : 0,
        brand: formData.brand.trim(),
        image: formData.image.trim(),
        featured: formData.featured,
        warrantyPeriod: formData.warrantyPeriod
          ? Number(formData.warrantyPeriod)
          : 0,
      };

      const response = await axios.post(
        "http://localhost:5000/api/products",
        payload,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      setSuccess("Product added successfully!");
      setCreatedProductId(response.data._id);

      // Reset form
      setFormData({
        name: "",
        description: "",
        price: "",
        category: "",
        stock: "",
        brand: "",
        image: "",
        featured: false,
        warrantyPeriod: "",
      });
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.response?.data?.error ||
        "Failed to add product";
      setError(errorMessage);
      console.error("Product creation error:", err.response?.data || err);
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (!isAdmin)
    return <div className="unauthorized">{error || "Unauthorized access"}</div>;

  return (
    <div className="product-form-container">
      <h2>Admin - Add New Product</h2>

      {success && (
        <div className="success-message">
          {success}{" "}
          {createdProductId && (
            <span>
              (Product ID: <strong>{createdProductId}</strong>)
            </span>
          )}
        </div>
      )}
      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        {/* All form groups same as before */}
        {/* ... */}
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            id="name"
            name="name"
            type="text"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="price">Price</label>
          <input
            id="price"
            name="price"
            type="text"
            value={formData.price}
            onChange={handleChange}
            required
            pattern="^\d+(\.\d{1,2})?$"
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">Category</label>
          <select
            id="category"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          >
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="stock">Stock</label>
          <input
            id="stock"
            name="stock"
            type="text"
            value={formData.stock}
            onChange={handleChange}
            pattern="^\d+$"
          />
        </div>

        <div className="form-group">
          <label htmlFor="brand">Brand</label>
          <input
            id="brand"
            name="brand"
            type="text"
            value={formData.brand}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="image">Image URL</label>
          <input
            id="image"
            name="image"
            type="text"
            value={formData.image}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="warrantyPeriod">Warranty Period (months)</label>
          <input
            id="warrantyPeriod"
            name="warrantyPeriod"
            type="text"
            value={formData.warrantyPeriod}
            onChange={handleChange}
            pattern="^\d+$"
          />
        </div>

        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id="featured"
            name="featured"
            checked={formData.featured}
            onChange={handleChange}
          />
          <label htmlFor="featured">Featured Product</label>
        </div>

        <button type="submit" disabled={submitting} className="submit-button">
          {submitting ? "Submitting..." : "Submit Product"}
        </button>
      </form>
    </div>
  );
};

export default ProductDashboard;
