import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaHeart, FaShoppingCart, FaTrash, FaArrowLeft } from "react-icons/fa";
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlistProducts, setWishlistProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [removing, setRemoving] = useState({});
  const [addingToCart, setAddingToCart] = useState({});
  const [successMessage, setSuccessMessage] = useState("");
  const [sortOption, setSortOption] = useState("default");
  const [filterInStock, setFilterInStock] = useState(false);
  const navigate = useNavigate();

  // Clear success message after 3 seconds
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage("");
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const fetchWishlist = useCallback(async () => {
    try {
      setLoading(true);
      setError("");

      const token = localStorage.getItem("token");
      if (!token) {
        setError("You must be logged in to view your wishlist.");
        setLoading(false);
        navigate("/login");
        return;
      }

      const response = await axios.get("http://localhost:5000/api/wishlist/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const wishlistItems = response.data?.wishlist || [];
      let products = [];

      if (wishlistItems.length > 0) {
        if (typeof wishlistItems[0] === "object" && wishlistItems[0]._id) {
          products = wishlistItems.map((item) => ({
            ...item,
            inWishlist: true,
          }));
        } else {
          const productDetails = await Promise.all(
            wishlistItems.map(async (productId) => {
              try {
                const res = await axios.get(
                  `http://localhost:5000/api/products/${productId}`
                );
                return { ...(res.data.product || res.data), inWishlist: true };
              } catch (error) {
                console.error(`Failed to fetch product ${productId}:`, error);
                return null;
              }
            })
          );
          products = productDetails.filter(Boolean);
        }
      }

      setWishlistProducts(products);
    } catch (err) {
      console.error("Error fetching wishlist:", err);
      setError(
        err.response?.data?.message ||
          err.message ||
          "Failed to fetch wishlist. Please try again."
      );
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  const removeFromWishlist = async (productId) => {
    try {
      setRemoving((prev) => ({ ...prev, [productId]: true }));
      const token = localStorage.getItem("token");

      setWishlistProducts((prev) =>
        prev.filter((item) => item._id !== productId)
      );

      const response = await axios.put(
        "http://localhost:5000/api/wishlist/remove",
        { productId },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!response.data.success) {
        fetchWishlist();
        throw new Error("Failed to remove from wishlist");
      }

      setSuccessMessage("Item removed from wishlist");
    } catch (err) {
      console.error("Error removing from wishlist:", err);
      setError(
        err.response?.data?.message ||
          "Failed to remove item. Please try again."
      );
      fetchWishlist();
    } finally {
      setRemoving((prev) => ({ ...prev, [productId]: false }));
    }
  };

  const addToCart = async (productId) => {
    try {
      setAddingToCart((prev) => ({ ...prev, [productId]: true }));
      const token = localStorage.getItem("token");

      await axios.post(
        "http://localhost:5000/api/cart/",
        { productId, quantity: 1 },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setSuccessMessage("Item added to cart successfully");
    } catch (err) {
      console.error("Error adding to cart:", err);
      setError(
        err.response?.data?.message ||
          "Failed to add item to cart. Please try again."
      );
    } finally {
      setAddingToCart((prev) => ({ ...prev, [productId]: false }));
    }
  };

  const moveToCart = async (productId) => {
    try {
      setAddingToCart((prev) => ({ ...prev, [productId]: true }));
      const token = localStorage.getItem("token");

      setWishlistProducts((prev) =>
        prev.filter((item) => item._id !== productId)
      );

      await axios.post(
        "http://localhost:5000/api/cart/",
        { productId, quantity: 1 },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      const removeResponse = await axios.put(
        "http://localhost:5000/api/wishlist/remove",
        { productId },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      if (!removeResponse.data.success) {
        throw new Error("Failed to remove from wishlist");
      }

      setSuccessMessage("Item moved to cart successfully");
    } catch (err) {
      console.error("Error moving to cart:", err);
      setError(
        err.response?.data?.message ||
          "Failed to move item to cart. Please try again."
      );
      fetchWishlist();
    } finally {
      setAddingToCart((prev) => ({ ...prev, [productId]: false }));
    }
  };

  const handleSortChange = (e) => {
    setSortOption(e.target.value);
  };

  const toggleFilterInStock = () => {
    setFilterInStock(!filterInStock);
  };

  const sortAndFilterProducts = () => {
    let filteredProducts = [...wishlistProducts];

    // Filter by stock status
    if (filterInStock) {
      filteredProducts = filteredProducts.filter((item) => item.stock > 0);
    }

    // Sort products
    switch (sortOption) {
      case "price-asc":
        return filteredProducts.sort((a, b) => a.price - b.price);
      case "price-desc":
        return filteredProducts.sort((a, b) => b.price - a.price);
      case "name-asc":
        return filteredProducts.sort((a, b) => a.name.localeCompare(b.name));
      case "name-desc":
        return filteredProducts.sort((a, b) => b.name.localeCompare(a.name));
      case "newest":
        return filteredProducts.sort(
          (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
        );
      case "oldest":
        return filteredProducts.sort(
          (a, b) => new Date(a.createdAt) - new Date(b.createdAt)
        );
      default:
        return filteredProducts;
    }
  };

  useEffect(() => {
    fetchWishlist();
  }, [fetchWishlist]);

  const handleProductClick = (productId, e) => {
    if (e.target.closest(".wishlist-action-btn")) return;
    navigate(`/product/${productId}`);
  };

  const handleBackToProducts = () => {
    navigate(-1); // Go back to previous page
  };

  const sortedAndFilteredProducts = sortAndFilterProducts();

  if (loading)
    return (
      <div className="loading-container">
        <div className="loading-spinner"></div>
        <p>Loading your wishlist...</p>
      </div>
    );

  if (error)
    return (
      <div className="error-container">
        <div className="error-message">
          <FaHeart className="error-icon" />
          <p>{error}</p>
          <div className="error-actions">
            <button onClick={fetchWishlist} className="retry-btn">
              Retry
            </button>
            {error.includes("logged in") && (
              <button onClick={() => navigate("/login")} className="login-btn">
                Login
              </button>
            )}
          </div>
        </div>
      </div>
    );

  return (
    <div className="wishlist-container">
      <button onClick={handleBackToProducts} className="back-button">
        <FaArrowLeft /> Back to Products
      </button>

      <div className="wishlist-header">
        <h2>
          <FaHeart className="wishlist-icon" /> My Wishlist
        </h2>

        <div className="wishlist-controls">
          <div className="sort-filter-container">
            <label htmlFor="sort-options">Sort by:</label>
            <select
              id="sort-options"
              value={sortOption}
              onChange={handleSortChange}
              className="sort-dropdown"
            >
              <option value="default">Default</option>
              <option value="price-asc">Price: Low to High</option>
              <option value="price-desc">Price: High to Low</option>
              <option value="name-asc">Name: A to Z</option>
              <option value="name-desc">Name: Z to A</option>
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
            </select>

            <label className="filter-checkbox">
              <input
                type="checkbox"
                checked={filterInStock}
                onChange={toggleFilterInStock}
              />
              Show only in-stock items
            </label>
          </div>
        </div>
      </div>

      {successMessage && (
        <div className="success-message">
          <FaShoppingCart /> {successMessage}
        </div>
      )}

      {sortedAndFilteredProducts.length === 0 && !loading ? (
        <div className="empty-wishlist">
          <FaHeart className="empty-icon" />
          <h3>Your wishlist is empty</h3>
          <p>Start adding items you love to see them here</p>
          <button onClick={() => navigate("/products")} className="browse-btn">
            Browse Products
          </button>
        </div>
      ) : (
        <>
          <div className="wishlist-summary">
            <p>
              Showing {sortedAndFilteredProducts.length} of{" "}
              {wishlistProducts.length}{" "}
              {wishlistProducts.length === 1 ? "item" : "items"}
              {filterInStock && " (in stock only)"}
            </p>
            <button
              onClick={() => navigate("/products")}
              className="continue-shopping-btn"
            >
              <FaShoppingCart /> Continue Shopping
            </button>
          </div>

          <div className="wishlist-items-container">
            {sortedAndFilteredProducts.map((item) => (
              <div
                key={item._id}
                className="wishlist-item-card"
                onClick={(e) => handleProductClick(item._id, e)}
              >
                <div className="wishlist-item-image-container">
                  <img
                    src={item.image || "/placeholder-image.jpg"}
                    alt={item.name}
                    className="wishlist-item-image"
                    onError={(e) => {
                      e.target.src = "/placeholder-image.jpg";
                    }}
                  />
                  {item.discount > 0 && (
                    <span className="discount-badge">-{item.discount}%</span>
                  )}
                </div>

                <div className="wishlist-item-details">
                  <h4 className="product-name">{item.name}</h4>
                  <div className="price-container">
                    {item.discount > 0 ? (
                      <>
                        <span className="original-price">
                          ${item.price.toFixed(2)}
                        </span>
                        <span className="discounted-price">
                          ${(item.price * (1 - item.discount / 100)).toFixed(2)}
                        </span>
                      </>
                    ) : (
                      <span className="current-price">
                        ${item.price.toFixed(2)}
                      </span>
                    )}
                  </div>

                  <div className="stock-status">
                    {item.stock > 0 ? (
                      <span className="in-stock">
                        <span className="stock-dot"></span> In Stock (
                        {item.stock} available)
                      </span>
                    ) : (
                      <span className="out-of-stock">
                        <span className="stock-dot"></span> Out of Stock
                      </span>
                    )}
                  </div>

                  <div className="wishlist-actions">
                    <button
                      className="wishlist-action-btn move-to-cart-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        moveToCart(item._id);
                      }}
                      disabled={addingToCart[item._id] || item.stock <= 0}
                      title="Move to Cart"
                    >
                      <FaShoppingCart />
                      {addingToCart[item._id] ? "Moving..." : "Move to Cart"}
                    </button>

                    <button
                      className="wishlist-action-btn remove-btn"
                      onClick={(e) => {
                        e.stopPropagation();
                        removeFromWishlist(item._id);
                      }}
                      disabled={removing[item._id]}
                      title="Remove from Wishlist"
                    >
                      <FaTrash />
                      {removing[item._id] ? "Removing..." : "Remove"}
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default Wishlist;
