import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlistProducts, setWishlistProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [removing, setRemoving] = useState(false);
  const navigate = useNavigate();

  // Memoize fetchWishlist to prevent unnecessary re-renders
  const fetchWishlist = useCallback(async () => {
    try {
      setLoading(true);
      setError("");

      const token = localStorage.getItem("token");

      if (!token) {
        setError("You must be logged in to view your wishlist.");
        setLoading(false);
        navigate("/login");
        return;
      }

      const response = await axios.get("http://localhost:5000/api/wishlist/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      console.log("Wishlist response:", response.data);
      const productIds = response.data?.wishlist || [];

      if (productIds.length === 0) {
        setWishlistProducts([]);
        setLoading(false);
        return;
      }

      const productDetails = await Promise.all(
        productIds.map(async (productId) => {
          console.log(productId);
          try {
            const res = await axios.get(
              `http://localhost:5000/api/products/${productId}`
            );
            return { ...(res.data.product || res.data), inWishlist: true };
          } catch (error) {
            console.error(`Failed to fetch product ${productId}`, error);
            return null;
          }
        })
      );

      setWishlistProducts(productDetails.filter(Boolean));
    } catch (err) {
      console.error("Error fetching wishlist:", err);
      setError(
        err.response?.data?.message ||
          "Failed to fetch wishlist. Please try again later."
      );
    } finally {
      setLoading(false);
    }
  }, [navigate]); // Added navigate as a dependency

  const removeFromWishlist = async (productId) => {
    try {
      setRemoving(true);
      const token = localStorage.getItem("token");

      await axios.put(
        "http://localhost:5000/api/wishlist/",
        { productId, action: "remove" },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      // Optimistic UI update
      setWishlistProducts((prev) =>
        prev.filter((product) => product._id !== productId)
      );
    } catch (err) {
      console.error("Error removing from wishlist:", err);
      // Show error message to user and attempt to refresh the wishlist
      setError(
        err.response?.data?.message ||
          "Failed to remove item. Please try again."
      );
      fetchWishlist();
    } finally {
      setRemoving(false);
    }
  };

  useEffect(() => {
    fetchWishlist(); // Now fetchWishlist is stable due to useCallback
  }, [fetchWishlist]);

  const handleProductClick = (productId, e) => {
    if (e.target.closest(".remove-btn")) return;
    navigate(`/product/${productId}`);
  };

  if (loading) return <div className="loading">Loading wishlist...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="wishlist-container">
      <h2>My Wishlist</h2>
      {wishlistProducts.length === 0 ? (
        <div className="empty-wishlist">
          <p>Your wishlist is empty.</p>
          <button onClick={() => navigate("/products")} className="browse-btn">
            Browse Products
          </button>
        </div>
      ) : (
        <ul className="wishlist-items">
          {wishlistProducts.map((item) => (
            <li
              key={item._id}
              className="wishlist-item"
              onClick={(e) => handleProductClick(item._id, e)}
            >
              <img
                src={item.image || "/placeholder-image.jpg"}
                alt={item.name}
                onError={(e) => {
                  e.target.src = "/placeholder-image.jpg"; // Handle missing image
                }}
              />
              <div className="wishlist-item-details">
                <h4>{item.name}</h4>
                <p className="description">
                  {item.description?.substring(0, 100)}...
                </p>
                <p className="price">
                  <strong>Price:</strong> ${item.price?.toFixed(2)}
                </p>
                <button
                  className="remove-btn"
                  onClick={() => removeFromWishlist(item._id)}
                  disabled={removing}
                >
                  {removing ? "Removing..." : "Remove"}
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Wishlist;
