import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlistProducts, setWishlistProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchWishlist = async () => {
      try {
        const token = localStorage.getItem("token");

        if (!token) {
          setError("You must be logged in to view your wishlist.");
          setLoading(false);
          return;
        }

        // Step 1: Fetch wishlist product IDs
        const { data } = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        const productIds = data.wishlist || [];

        if (productIds.length === 0) {
          setWishlistProducts([]);
          setLoading(false);
          return;
        }

        // Step 2: Fetch product details for all IDs
        const productDetails = await Promise.all(
          productIds.map(async (productId) => {
            try {
              const res = await axios.get(
                `http://localhost:5000/api/products/${productId}`
              );
              return res.data;
            } catch (productError) {
              console.error(
                `Failed to fetch product ${productId}`,
                productError
              );
              return null; // skip if product fetch fails
            }
          })
        );

        // Remove any null responses (failed fetches)
        const validProducts = productDetails.filter(
          (product) => product !== null
        );

        setWishlistProducts(validProducts);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching wishlist:", err);
        setError("Failed to fetch wishlist. Please try again later.");
        setLoading(false);
      }
    };

    fetchWishlist();
  }, []);

  const handleAddToWishlist = (productId) => {
    navigate(`/product/${productId}`);
  };

  if (loading) return <p>Loading wishlist...</p>;
  if (error) return <p style={{ color: "red" }}>{error}</p>;

  return (
    <div className="wishlist-container">
      <h2>My Wishlist</h2>
      {wishlistProducts.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <ul className="wishlist-items">
          {wishlistProducts.map((item) => (
            <li
              key={item._id}
              className="wishlist-item"
              onClick={() => handleProductClick(item._id)}
              style={{ cursor: "pointer" }}
            >
              <img src={item.image} alt={item.name} />
              <div>
                <h4>{item.name}</h4>
                <p>{item.description}</p>
                <p>
                  <strong>Price:</strong> ${item.price}
                </p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Wishlist;
