import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./AdminProductForm.css";

const ProductDashboard = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [createdProductId, setCreatedProductId] = useState("");
  const [imagePreview, setImagePreview] = useState("");
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    stock: "",
    brand: "",
    image: "",
    featured: false,
    warrantyPeriod: "",
  });

  const categories = [
    "Electronics",
    "Clothing",
    "Home & Garden",
    "Books",
    "Toys",
    "Sports",
    "Health & Beauty",
  ];

  // ... (keep existing useEffect and verifyAdminStatus)

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
        setFormData({ ...formData, image: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handlePriceChange = (e) => {
    const value = e.target.value;
    if (/^\d*\.?\d*$/.test(value)) {
      setFormData({ ...formData, price: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setCreatedProductId("");

    if (!validateForm()) return;
    setSubmitting(true);

    try {
      const token = localStorage.getItem("token");
      const payload = {
        ...formData,
        price: Number(formData.price),
        stock: formData.stock ? Number(formData.stock) : 0,
        warrantyPeriod: formData.warrantyPeriod
          ? Number(formData.warrantyPeriod)
          : 0,
      };

      const response = await axios.post(
        "http://localhost:5000/api/products",
        payload,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      setSuccess("Product added successfully!");
      setCreatedProductId(response.data._id);
      setTimeout(() => {
        navigate(`/product/${response.data._id}`);
      }, 2000);

      // Reset form
      setFormData({
        name: "",
        description: "",
        price: "",
        category: "",
        stock: "",
        brand: "",
        image: "",
        featured: false,
        warrantyPeriod: "",
      });
      setImagePreview("");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.response?.data?.error ||
        "Failed to add product";
      setError(errorMessage);
      console.error("Product creation error:", err.response?.data || err);
    } finally {
      setSubmitting(false);
    }
  };

  // ... (rest of the component remains similar with the updated form inputs)

  if (loading) return <div className="loading">Loading...</div>;
  if (!isAdmin)
    return <div className="unauthorized">{error || "Unauthorized access"}</div>;

  return (
    <div className="product-form-container">
      <h2>Admin - Add New Product</h2>

      {success && (
        <div className="success-message">
          {success}{" "}
          {createdProductId && (
            <span>
              (Product ID: <strong>{createdProductId}</strong>)
            </span>
          )}
        </div>
      )}
      {error && <div className="error-message">{error}</div>}

      <form onSubmit={handleSubmit}>
        {/* All form groups same as before */}
        {/* ... */}
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input
            id="name"
            name="name"
            type="text"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="price">Price</label>
          <input
            id="price"
            name="price"
            type="text"
            value={formData.price}
            onChange={handleChange}
            required
            pattern="^\d+(\.\d{1,2})?$"
          />
        </div>

        <div className="form-group">
          <label htmlFor="category">Category</label>
          <select
            id="category"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          >
            <option value="">Select a category</option>
            {categories.map((category) => (
              <option key={category} value={category}>
                {category}
              </option>
            ))}
          </select>
        </div>

        <div className="form-group">
          <label htmlFor="stock">Stock</label>
          <input
            id="stock"
            name="stock"
            type="text"
            value={formData.stock}
            onChange={handleChange}
            pattern="^\d+$"
          />
        </div>

        <div className="form-group">
          <label htmlFor="brand">Brand</label>
          <input
            id="brand"
            name="brand"
            type="text"
            value={formData.brand}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="image">Image URL</label>
          <input
            id="image"
            name="image"
            type="text"
            value={formData.image}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label htmlFor="warrantyPeriod">Warranty Period (months)</label>
          <input
            id="warrantyPeriod"
            name="warrantyPeriod"
            type="text"
            value={formData.warrantyPeriod}
            onChange={handleChange}
            pattern="^\d+$"
          />
        </div>

        <div className="form-group checkbox-group">
          <input
            type="checkbox"
            id="featured"
            name="featured"
            checked={formData.featured}
            onChange={handleChange}
          />
          <label htmlFor="featured">Featured Product</label>
        </div>

        <button type="submit" disabled={submitting} className="submit-button">
          {submitting ? "Submitting..." : "Submit Product"}
        </button>
      </form>
    </div>
  );
};

export default ProductDashboard;
