import React, { useState } from "react";
import axios from "axios";

const AdminProductForm = () => {
  const [formData, setFormData] = useState({
    name: "iPhone 14",
    description: "Latest iPhone",
    price: 999,
    category: "Electronics",
    stock: 50,
    image:
      "https://cdn.wccftech.com/wp-content/uploads/2023/04/14-iphone-1.jpeg",
  });

  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem("token"); // if using JWT
      const res = await axios.post("/api/admin/add-product", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setSuccess("Product added successfully!");
      setError("");
    } catch (err) {
      setError(err.response?.data?.message || "Failed to add product");
      setSuccess("");
    }
  };

  return (
    <div
      className="admin-form-container"
      style={{ maxWidth: "600px", margin: "auto", padding: "2rem" }}
    >
      <h2>Add New Product (Admin)</h2>
      {success && <p style={{ color: "green" }}>{success}</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      <form onSubmit={handleSubmit}>
        {["name", "description", "price", "category", "stock", "image"].map(
          (field) => (
            <div key={field} style={{ marginBottom: "1rem" }}>
              <label>{field.charAt(0).toUpperCase() + field.slice(1)}</label>
              <input
                type={
                  field === "price" || field === "stock" ? "number" : "text"
                }
                name={field}
                value={formData[field]}
                onChange={handleChange}
                style={{ width: "100%", padding: "0.5rem" }}
                required
              />
            </div>
          )
        )}
        <button
          type="submit"
          style={{
            padding: "0.6rem 1.5rem",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
          }}
        >
          Submit Product
        </button>
      </form>
    </div>
  );
};

export default AdminProductForm;
