import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlist, setWishlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchWishlist = async () => {
      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );
        const user1 = await User.findById(req.user._id).populate("wishlist");
        console.log(user1.wishlist[0]); // Check first item
        console.log("Fetched Wishlist Data:", data); // Log the response data to see its structure

        // Ensure that the data is an array before setting the state
        if (Array.isArray(data)) {
          setWishlist(data); // Set the full product details from the response
        } else {
          setError("Wishlist data is not in the expected format.");
        }
      } catch (error) {
        console.error("Error fetching wishlist:", error);
        setError("Error fetching wishlist.");
      } finally {
        setLoading(false);
      }
    };

    fetchWishlist();
  }, []); // Empty array means it will run once when the component is mounted

  return (
    <div className="dashboard">
      <h2 className="dashboard-title">My Wishlist</h2>

      {loading ? (
        <p>Loading your wishlist...</p>
      ) : error ? (
        <p>{error}</p>
      ) : wishlist.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <div className="product-grid">
          {wishlist.map((product) => (
            <div className="product-card" key={product._id}>
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p className="price">${product.price}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
