import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const [userInfo, setUserInfo] = useState(null);
  const [adminProducts, setAdminProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [hoverStates, setHoverStates] = useState({});

  useEffect(() => {
    if (!token) {
      setError("No token found. Please log in.");
      setLoading(false);
      navigate("/login");
      return;
    }

    fetchUserData();
  }, [token, navigate]);

  const fetchUserData = async () => {
    try {
      setLoading(true);
      const { data: user } = await axios.get(
        "http://localhost:5000/api/users/profile",
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUserInfo(user);

      if (user.isAdmin && user.productsAdded?.length > 0) {
        const productResponses = await Promise.all(
          user.productsAdded.map((id) =>
            axios
              .get(`http://localhost:5000/api/products/${id}`)
              .then((res) => res.data)
              .catch(() => null)
          )
        );

        const validProducts = productResponses.filter((p) => p !== null);
        setAdminProducts(validProducts);
      } else {
        setAdminProducts([]);
      }
    } catch (err) {
      console.error("Error:", err);
      if (err.response?.status === 401) {
        localStorage.removeItem("token");
        navigate("/login");
      }
      setError(err.response?.data?.message || "Failed to fetch data");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleProductClick = (id) => {
    navigate(`/product/${id}`);
  };

  const handleMouseEnter = (productId) => {
    setHoverStates((prev) => ({ ...prev, [productId]: true }));
  };

  const handleMouseLeave = (productId) => {
    setHoverStates((prev) => ({ ...prev, [productId]: false }));
  };

  const handleDeleteProduct = async (productId, e) => {
    e.stopPropagation();
    if (window.confirm("Are you sure you want to delete this product?")) {
      try {
        await axios.delete(`http://localhost:5000/api/products/${productId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });

        // Optimistically update the UI
        setAdminProducts((prev) => prev.filter((p) => p._id !== productId));

        // Also update userInfo to remove the product from productsAdded
        setUserInfo((prev) => ({
          ...prev,
          productsAdded: prev.productsAdded.filter((id) => id !== productId),
        }));

        alert("Product deleted successfully");
      } catch (error) {
        console.error("Error deleting product:", error);
        alert(error.response?.data?.message || "Failed to delete product");
        // Refresh data if something went wrong
        fetchUserData();
      }
    }
  };

  const handleUpdateProduct = (productId, e) => {
    e.stopPropagation();
    navigate(`/update-product/${productId}`);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.header}>User Profile</h2>
      {loading ? (
        <div style={styles.loadingContainer}>
          <div style={styles.spinner}></div>
          <p>Loading your profile...</p>
        </div>
      ) : error ? (
        <p style={styles.errorText}>{error}</p>
      ) : userInfo ? (
        <div style={styles.profileContainer}>
          <div style={styles.userInfoSection}>
            <h3 style={styles.username}>{userInfo.name}</h3>
            <p style={styles.infoText}>Email: {userInfo.email}</p>
            <p style={styles.infoText}>
              Joined: {new Date(userInfo.createdAt).toLocaleDateString()}
            </p>
            <p style={styles.infoText}>
              Role: {userInfo.isAdmin ? "Administrator" : "User"}
            </p>
          </div>

          {userInfo.isAdmin && (
            <div style={styles.adminSection}>
              <h3 style={styles.sectionTitle}>Your Added Products</h3>
              {adminProducts.length > 0 ? (
                <div style={styles.productsList}>
                  {adminProducts.map((product) => (
                    <div
                      key={product._id}
                      style={{
                        ...styles.productItem,
                        ...(hoverStates[product._id]
                          ? styles.productItemHover
                          : {}),
                      }}
                      onClick={() => handleProductClick(product._id)}
                      onMouseEnter={() => handleMouseEnter(product._id)}
                      onMouseLeave={() => handleMouseLeave(product._id)}
                    >
                      <img
                        src={product.image}
                        alt={product.name}
                        style={styles.productImage}
                      />
                      <div style={styles.productDetails}>
                        <strong style={styles.productTitle}>
                          {product.name}
                        </strong>
                        <p style={styles.productPrice}>
                          ${product.price.toFixed(2)}
                        </p>
                        <p style={styles.productCategory}>{product.category}</p>
                      </div>
                      <div style={styles.productActions}>
                        <button
                          style={styles.updateButton}
                          onClick={(e) => handleUpdateProduct(product._id, e)}
                        >
                          Update
                        </button>
                        <button
                          style={styles.deleteButton}
                          onClick={(e) => handleDeleteProduct(product._id, e)}
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <p style={styles.noProductsText}>No products added yet</p>
              )}
            </div>
          )}

          <button
            onClick={handleLogout}
            style={styles.logoutBtn}
            onMouseEnter={(e) =>
              (e.currentTarget.style.backgroundColor = "#c82333")
            }
            onMouseLeave={(e) =>
              (e.currentTarget.style.backgroundColor = "#dc3545")
            }
          >
            Logout
          </button>
        </div>
      ) : (
        <p>Please log in to view your profile.</p>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "2rem",
    maxWidth: "900px",
    margin: "2rem auto",
    backgroundColor: "#f4f7fa",
    borderRadius: "12px",
    boxShadow: "0 8px 24px rgba(0, 0, 0, 0.06)",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  },
  header: {
    color: "#2c3e50",
    textAlign: "center",
    marginBottom: "1.5rem",
  },
  loadingContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    padding: "2rem",
  },
  spinner: {
    border: "4px solid rgba(0, 0, 0, 0.1)",
    width: "36px",
    height: "36px",
    borderRadius: "50%",
    borderLeftColor: "#09f",
    animation: "spin 1s linear infinite",
    marginBottom: "1rem",
  },
  profileContainer: {
    backgroundColor: "#ffffff",
    padding: "2rem",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
    marginBottom: "2rem",
    border: "1px solid #e0e0e0",
  },
  userInfoSection: {
    marginBottom: "2rem",
    paddingBottom: "1.5rem",
    borderBottom: "1px solid #eee",
  },
  username: {
    fontSize: "1.8rem",
    fontWeight: "700",
    color: "#2c3e50",
    marginBottom: "0.5rem",
  },
  infoText: {
    fontSize: "1.1rem",
    color: "#555",
    margin: "0.5rem 0",
    lineHeight: "1.5",
  },
  logoutBtn: {
    padding: "0.6rem 1.4rem",
    backgroundColor: "#dc3545",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: "1.5rem",
    fontSize: "1rem",
    transition: "all 0.3s ease",
    width: "100%",
    maxWidth: "200px",
  },
  errorText: {
    color: "#dc3545",
    fontSize: "1.2rem",
    fontWeight: "500",
    marginTop: "1rem",
    textAlign: "center",
    padding: "1rem",
    backgroundColor: "#f8d7da",
    borderRadius: "8px",
    border: "1px solid #f5c6cb",
  },
  adminSection: {
    marginTop: "2rem",
    backgroundColor: "#e9f2ff",
