import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "./Dashboard.css";

const ProductDashboard = () => {
  const [products, setProducts] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [cart, setCart] = useState([]);
  const [token] = useState(localStorage.getItem("token"));
  const [searchTerm, setSearchTerm] = useState("");
  const [userInfo, setUserInfo] = useState(null);

  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const { data } = await axios.get("http://localhost:5000/api/products");
        setProducts(data);
      } catch (err) {
        console.error("Error fetching products:", err);
      }
    };

    const fetchWishlist = async () => {
      if (token) {
        try {
          const { data } = await axios.get(
            "http://localhost:5000/api/users/wishlist",
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          setWishlist(data.wishlist || []);
        } catch (err) {
          console.error("Error fetching wishlist:", err);
        }
      }
    };

    const fetchUserInfo = async () => {
      if (token) {
        try {
          const { data } = await axios.get(
            "http://localhost:5000/api/users/me",
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          setUserInfo(data);
        } catch (err) {
          console.error("Error fetching user info:", err);
        }
      }
    };

    fetchProducts();
    fetchWishlist();
    fetchUserInfo();
  }, [token]);

  const toggleWishlist = async (productId) => {
    if (token) {
      try {
        if (wishlist.includes(productId)) {
          await axios.put(
            "http://localhost:5000/api/users/wishlist/remove",
            { productId },
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setWishlist((prev) => prev.filter((id) => id !== productId));
        } else {
          await axios.put(
            "http://localhost:5000/api/users/wishlist",
            { productId },
            { headers: { Authorization: `Bearer ${token}` } }
          );
          setWishlist((prev) => [...prev, productId]);
        }
      } catch (err) {
        console.error("Error updating wishlist:", err);
      }
    }
  };

  const toggleCart = (productId) => {
    setCart((prev) =>
      prev.includes(productId)
        ? prev.filter((id) => id !== productId)
        : [...prev, productId]
    );
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h2>ShopEasy</h2>
        <div className="header-actions">
          {userInfo && (
            <div className="profile-dropdown">
              <button className="profile-btn">
                üë§ {userInfo.name || userInfo.email}
              </button>
              <div className="profile-dropdown">
                <button
                  className="profile-btn"
                  onClick={() => setShowDropdown((prev) => !prev)}
                >
                  üë§ {userInfo.name || userInfo.email}
                </button>

                {showDropdown && (
                  <div className="dropdown-content">
                    <p>
                      <strong>Name:</strong> {userInfo.name || "N/A"}
                    </p>
                    <p>
                      <strong>Email:</strong> {userInfo.email}
                    </p>
                    <p>
                      <strong>Role:</strong> {userInfo.role}
                    </p>
                    <Link to="/profile">
                      <button className="profile-details-btn">
                        View Profile
                      </button>
                    </Link>
                    {userInfo.role === "admin" && (
                      <Link to="/admin">
                        <button className="admin-btn">Admin Dashboard</button>
                      </Link>
                    )}
                  </div>
                )}
              </div>
            </div>
          )}
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </header>

      <div className="search-container">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="wishlist-button">
        <Link to="/wishlist">
          <button className="wishlist-btn">Go to Wishlist</button>
        </Link>
      </div>

      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div className="product-card" key={product._id}>
            <Link to={`/product/${product._id}`}>
              <img
                src={product.image || "https://via.placeholder.com/300x200"}
                alt={product.name}
              />
              <h3>{product.name}</h3>
              <p className="category">{product.category}</p>
              <p className="price">${product.price}</p>
              <div className="details">
                <span>‚≠ê {product.rating}</span>
                <span>Stock: {product.stock}</span>
              </div>
              {/* Unique Tags */}
              <div className="tags">
                {product.stock <= 5 && (
                  <span className="tag low-stock">Low Stock</span>
                )}
                {product.rating >= 4.5 && (
                  <span className="tag best-seller">Best Seller</span>
                )}
                {product.category === "New" && (
                  <span className="tag new">New</span>
                )}
              </div>
            </Link>

            <div className="actions">
              <label>
                <input
                  type="checkbox"
                  checked={wishlist.includes(product._id)}
                  onChange={() => toggleWishlist(product._id)}
                />{" "}
                Wishlist
              </label>
              <label>
                <input
                  type="checkbox"
                  checked={cart.includes(product._id)}
                  onChange={() => toggleCart(product._id)}
                />{" "}
                Cart
              </label>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductDashboard;
