import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const VerifyAdmin = ({ setIsAdmin, setError, setLoading }) => {
  const history = useHistory();

  useEffect(() => {
    const verifyAdminStatus = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        setError("Please login to access this page");
        history.push("/login");
        return;
      }

      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/profile",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (data.isAdmin) {
          setIsAdmin(true);
        } else {
          // Check if user has already been prompted
          const alreadyPrompted = localStorage.getItem("adminPrompted");

          if (!alreadyPrompted) {
            const confirmAccess = window.confirm(
              "You are not an admin. Do you want to request admin access?"
            );

            if (confirmAccess) {
              try {
                // Call API to request admin access
                await axios.put(
                  `http://localhost:5000/api/users/${data._id}/request-admin`,
                  {},
                  { headers: { Authorization: `Bearer ${token}` } }
                );

                setError("Admin access requested. Please wait for approval.");
                localStorage.setItem("adminPrompted", "true");
              } catch (err) {
                setError(
                  "Failed to request admin access. Please contact support."
                );
              }
            }

            history.push("/");
          } else {
            setError(
              "Admin access already requested. Please wait for approval."
            );
          }
        }
      } catch (err) {
        const errorMessage =
          err.response?.data?.message || "Failed to verify admin status";
        setError(errorMessage);
        console.error("Admin verification error:", err);
        history.push("/login");
      } finally {
        setLoading(false);
      }
    };

    verifyAdminStatus();
  }, [setIsAdmin, setError, setLoading, history]);

  return null;
};

export default VerifyAdmin;
