import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const VerifyAdmin = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");

  useEffect(() => {
    const verifyAdmin = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        setError("Login required");
        return navigate("/login");
      }

      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/profile",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (data.isAdmin) {
          navigate("/admin/products");
        } else {
          const alreadyPrompted = localStorage.getItem("adminPrompted");

          if (!alreadyPrompted) {
            const confirm = window.confirm(
              "You're not an admin. Request admin access?"
            );
            if (confirm) {
              await axios.put(
                `http://localhost:5000/api/users/${data._id}/request-admin`,
                {},
                { headers: { Authorization: `Bearer ${token}` } }
              );
              localStorage.setItem("adminPrompted", "true");
              setSuccess("Admin access requested.");
            }
            navigate("/");
          } else {
            setError(
              "Admin access already requested. Please wait for approval."
            );
            navigate("/");
          }
        }
      } catch (err) {
        setError("Verification failed");
        navigate("/login");
      } finally {
        setLoading(false);
      }
    };

    verifyAdmin();
  }, [navigate]);

  if (loading) return <div>Loading...</div>;
  return <div>{error || success}</div>;
};

export default VerifyAdmin;
