import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const Profile = () => {
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  const [userInfo, setUserInfo] = useState(null);
  const [adminProducts, setAdminProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!token) {
      setError("No token found. Please log in.");
      setLoading(false);
      return;
    }

    const fetchUserInfo = async () => {
      try {
        const response = await axios.get(
          "http://localhost:5000/api/users/profile",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        const user = response.data;
        setUserInfo(user);

        if (user.isAdmin && user.productsAdded?.length > 0) {
          const productPromises = user.productsAdded.map((id) =>
            axios.get(`http://localhost:5000/api/products/${id}`)
          );
          const productResponses = await Promise.all(productPromises);
          setAdminProducts(productResponses.map((res) => res.data));
        }
      } catch (err) {
        console.error("Error:", err);
        setError(err.response?.data?.message || "Failed to fetch data");
      } finally {
        setLoading(false);
      }
    };

    fetchUserInfo();
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleProductClick = (id) => {
    navigate(`/product/${id}`);
  };

  // New: Delete product handler
  const handleDeleteProduct = async (productId) => {
    const confirmDelete = window.confirm(
      "Are you sure you want to remove this product from your profile?"
    );
    if (!confirmDelete) return;

    try {
      // Assuming your backend has an endpoint to update user's productsAdded array, e.g. PATCH /api/users/removeProduct
      await axios.patch(
        "http://localhost:5000/api/users/removeProduct",
        { productId },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Update local state to remove the product from UI immediately
      setAdminProducts((prev) =>
        prev.filter((product) => product._id !== productId)
      );

      // Also update userInfo.productsAdded to keep state consistent
      setUserInfo((prev) => ({
        ...prev,
        productsAdded: prev.productsAdded.filter((id) => id !== productId),
      }));
    } catch (err) {
      alert(
        err.response?.data?.message ||
          "Failed to remove the product. Please try again."
      );
    }
  };

  return (
    <div style={styles.container}>
      <h2>User Profile</h2>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p style={styles.errorText}>{error}</p>
      ) : userInfo ? (
        <div style={styles.profileContainer}>
          <h3 style={styles.username}>{userInfo.name}</h3>
          <p style={styles.infoText}>Email: {userInfo.email}</p>
          <p style={styles.infoText}>
            Joined: {new Date(userInfo.createdAt).toLocaleDateString()}
          </p>

          {userInfo.isAdmin && adminProducts.length > 0 && (
            <div style={styles.adminSection}>
              <h3>Your Added Products</h3>
              <div style={styles.productsList}>
                {adminProducts.map((product) => (
                  <div
                    key={product._id}
                    style={styles.productItem}
                    // Clicking product div navigates to product detail
                    onClick={() => handleProductClick(product._id)}
                  >
                    <img
                      src={product.image}
                      alt={product.name}
                      style={styles.productImage}
                    />
                    <div style={styles.productDetails}>
                      <strong>{product.name}</strong>
                      <p>${product.price}</p>
                      <p>{product.category}</p>
                    </div>

                    {/* Prevent navigation when clicking Delete button */}
                    <button
                      style={styles.deleteBtn}
                      onClick={(e) => {
                        e.stopPropagation(); // Prevent div click event
                        handleDeleteProduct(product._id);
                      }}
                    >
                      Delete
                    </button>
                  </div>
                ))}
              </div>
            </div>
          )}

          <button onClick={handleLogout} style={styles.logoutBtn}>
            Logout
          </button>
        </div>
      ) : (
        <p>Please log in to view your profile.</p>
      )}
    </div>
  );
};

const styles = {
  container: {
    padding: "2rem",
    maxWidth: "900px",
    margin: "2rem auto",
    backgroundColor: "#f4f7fa",
    borderRadius: "12px",
    boxShadow: "0 8px 24px rgba(0, 0, 0, 0.06)",
  },

  profileContainer: {
    backgroundColor: "#ffffff",
    padding: "2rem",
    borderRadius: "12px",
    boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
    marginBottom: "2rem",
    border: "1px solid #e0e0e0",
  },

  username: {
    fontSize: "2rem",
    fontWeight: "700",
    color: "#2c3e50",
    marginBottom: "0.5rem",
  },

  infoText: {
    fontSize: "1.1rem",
    color: "#555",
    margin: "0.3rem 0",
  },

  logoutBtn: {
    padding: "0.6rem 1.4rem",
    backgroundColor: "#dc3545",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    marginTop: "1.5rem",
    fontSize: "1rem",
    transition: "all 0.3s ease",
  },

  errorText: {
    color: "#dc3545",
    fontSize: "1.2rem",
    fontWeight: "500",
    marginTop: "1rem",
  },

  adminSection: {
    marginTop: "2rem",
    backgroundColor: "#e9f2ff",
    padding: "1.5rem",
    borderRadius: "10px",
    border: "1px solid #d0e3ff",
  },

  productsList: {
    marginTop: "1.5rem",
    display: "flex",
    flexDirection: "column",
    gap: "1.2rem",
  },

  productItem: {
    display: "flex",
    alignItems: "center",
    gap: "1rem",
    padding: "1rem",
    backgroundColor: "#ffffff",
    borderRadius: "10px",
    boxShadow: "0 2px 8px rgba(0, 0, 0, 0.05)",
    transition: "transform 0.2s ease, box-shadow 0.2s ease",
    border: "1px solid #e6e6e6",
    cursor: "pointer",
    position: "relative", // so Delete button can be positioned if needed
  },

  productImage: {
    width: "70px",
    height: "70px",
    objectFit: "cover",
    borderRadius: "8px",
    border: "1px solid #ddd",
  },

  productDetails: {
    display: "flex",
    flexDirection: "column",
    flexGrow: 1,
  },

  deleteBtn: {
    padding: "0.4rem 0.8rem",
    backgroundColor: "#dc3545",
    color: "white",
    border: "none",
    borderRadius: "6px",
    cursor: "pointer",
    fontWeight: "600",
    fontSize: "0.9rem",
    transition: "background-color 0.3s ease",
  },

  deleteBtnHover: {
    backgroundColor: "#c82333",
  },
};

export default Profile;
