const Wishlist = () => {
  const [wishlist, setWishlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchWishlist = async () => {
      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        // Debug: Check the actual structure
        console.log("API Response:", data);

        // Handle both possible response structures
        const products = data.wishlist || data;

        if (!Array.isArray(products)) {
          throw new Error("Invalid wishlist data format");
        }

        setWishlist(products);
      } catch (error) {
        console.error("Fetch error:", error);
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchWishlist();
  }, []);

  // Improved render method
  const renderProduct = (product) => {
    // Fallbacks for missing data
    const imageUrl =
      product.image || product.images?.[0] || "/default-product.png";
    const productName = product.name || "Unnamed Product";
    const priceDisplay = product.price
      ? `$${product.price.toFixed(2)}`
      : "Price not available";

    return (
      <div className="product-card" key={product._id}>
        <img
          src={imageUrl}
          alt={productName}
          onError={(e) => (e.target.src = "/default-product.png")}
        />
        <h3>{productName}</h3>
        <p className="price">{priceDisplay}</p>
      </div>
    );
  };

  return (
    <div className="dashboard">
      <h2 className="dashboard-title">My Wishlist</h2>

      {loading ? (
        <p>Loading your wishlist...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : wishlist.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <div className="product-grid">{wishlist.map(renderProduct)}</div>
      )}
    </div>
  );
};
