import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./AdminProductForm.css";

const ProductDashboard = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [createdProductId, setCreatedProductId] = useState("");
  const [imagePreview, setImagePreview] = useState("");
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: "",
    category: "",
    stock: "",
    brand: "",
    image: "",
    featured: false,
    warrantyPeriod: "",
  });

  const categories = [
    "Electronics",
    "Clothing",
    "Home & Garden",
    "Books",
    "Toys",
    "Sports",
    "Health & Beauty",
  ];

  // ... (keep existing useEffect and verifyAdminStatus)

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
        setFormData({ ...formData, image: reader.result });
      };
      reader.readAsDataURL(file);
    }
  };

  const handlePriceChange = (e) => {
    const value = e.target.value;
    if (/^\d*\.?\d*$/.test(value)) {
      setFormData({ ...formData, price: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setCreatedProductId("");

    if (!validateForm()) return;
    setSubmitting(true);

    try {
      const token = localStorage.getItem("token");
      const payload = {
        ...formData,
        price: Number(formData.price),
        stock: formData.stock ? Number(formData.stock) : 0,
        warrantyPeriod: formData.warrantyPeriod
          ? Number(formData.warrantyPeriod)
          : 0,
      };

      const response = await axios.post(
        "http://localhost:5000/api/products",
        payload,
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );

      setSuccess("Product added successfully!");
      setCreatedProductId(response.data._id);
      setTimeout(() => {
        navigate(`/product/${response.data._id}`);
      }, 2000);

      // Reset form
      setFormData({
        name: "",
        description: "",
        price: "",
        category: "",
        stock: "",
        brand: "",
        image: "",
        featured: false,
        warrantyPeriod: "",
      });
      setImagePreview("");
    } catch (err) {
      const errorMessage =
        err.response?.data?.message ||
        err.response?.data?.error ||
        "Failed to add product";
      setError(errorMessage);
      console.error("Product creation error:", err.response?.data || err);
    } finally {
      setSubmitting(false);
    }
  };

  // ... (rest of the component remains similar with the updated form inputs)
};

export default ProductDashboard;
