import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./Dashboard.css";

const ProductDashboard = () => {
  const [products, setProducts] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [cart, setCart] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [token] = useState(localStorage.getItem("token"));
  const [loading, setLoading] = useState({
    products: true,
    wishlist: true,
  });
  const [wishlistStatus, setWishlistStatus] = useState({});

  // Initialize wishlist status
  useEffect(() => {
    const status = {};
    wishlist.forEach((productId) => {
      status[productId] = true;
    });
    setWishlistStatus(status);
  }, [wishlist]);

  // Fetch products and wishlist on component mount
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Fetch products
        const productsResponse = await axios.get(
          "http://localhost:5000/api/products"
        );
        setProducts(productsResponse.data);
        setLoading((prev) => ({ ...prev, products: false }));

        // Fetch wishlist if token exists
        if (token) {
          const wishlistResponse = await axios.get(
            "http://localhost:5000/api/wishlist/",
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );
          const wishlistData = wishlistResponse.data.wishlist || [];
          setWishlist(wishlistData);

          // Initialize wishlist status
          const initialStatus = {};
          wishlistData.forEach((productId) => {
            initialStatus[productId] = true;
          });
          setWishlistStatus(initialStatus);

          setLoading((prev) => ({ ...prev, wishlist: false }));
        } else {
          setLoading((prev) => ({ ...prev, wishlist: false }));
        }
      } catch (err) {
        console.error("Error fetching data:", err);
        setLoading({ products: false, wishlist: false });
      }
    };

    fetchData();
  }, [token]);

  // Toggle product in wishlist
  const toggleWishlist = async (productId) => {
    if (!token) {
      console.log("Please login to manage your wishlist.");
      return;
    }

    const isInWishlist = wishlistStatus[productId];

    try {
      // Optimistic UI update
      setWishlistStatus((prev) => ({
        ...prev,
        [productId]: !isInWishlist,
      }));

      if (isInWishlist) {
        // Remove from wishlist UI state
        setWishlist((prev) => prev.filter((id) => id !== productId));
      } else {
        // Add to wishlist UI state
        setWishlist((prev) => [...prev, productId]);
      }

      // Make API call
      await axios.put(
        "http://localhost:5000/api/wishlist",
        {
          productId,
          action: isInWishlist ? "remove" : "add", // Unified endpoint with action
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
    } catch (err) {
      console.error("Error updating wishlist:", err);

      // Revert optimistic UI update on error
      setWishlistStatus((prev) => ({
        ...prev,
        [productId]: isInWishlist,
      }));

      if (isInWishlist) {
        setWishlist((prev) => [...prev, productId]);
      } else {
        setWishlist((prev) => prev.filter((id) => id !== productId));
      }
    }
  };

  // Toggle product in cart
  const toggleCart = (productId) => {
    setCart((prev) =>
      prev.includes(productId)
        ? prev.filter((id) => id !== productId)
        : [...prev, productId]
    );
  };

  const filteredProducts = products.filter(
    (product) =>
      product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.category.toLowerCase().includes(searchQuery.toLowerCase())
  );

  if (loading.products) {
    return <div className="loading">Loading products...</div>;
  }

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="dashboard-header">
        <h1>My Shop</h1>
        <input
          type="text"
          placeholder="Search by name or category..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-bar"
        />
        <Link to="/wishlist">
          <button className="wishlist-btn">Go to Wishlist</button>
        </Link>
        <Link to="/profile">
          <button className="profile-btn">Profile</button>
        </Link>
      </header>

      {/* Title */}
      <h2 className="dashboard-title">Product Dashboard</h2>

      {/* Product Grid */}
      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div className="product-card" key={product._id}>
            <Link to={`/product/${product._id}`}>
              <img
                src={product.image || "https://via.placeholder.com/300x200"}
                alt={product.name}
              />
              <h3>{product.name}</h3>
              <p className="category">{product.category}</p>
              <p className="price">
                {product.price.toLocaleString("en-IN", {
                  style: "currency",
                  currency: "INR",
                })}
              </p>
              <div className="details">
                <span>‚≠ê {product.rating}</span>
                <span>Stock: {product.stock}</span>
              </div>
            </Link>
            <div className="actions">
              <label>
                <input
                  type="checkbox"
                  checked={!!wishlistStatus[product._id]}
                  onChange={() => toggleWishlist(product._id)}
                  disabled={loading.wishlist}
                />{" "}
                Wishlist
              </label>
              <label>
                <input
                  type="checkbox"
                  checked={cart.includes(product._id)}
                  onChange={() => toggleCart(product._id)}
                />{" "}
                Cart
              </label>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductDashboard;
