import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlist, setWishlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchWishlist = async () => {
      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        console.log("Full API Response:", data);
        console.log("Wishlist Array:", data.wishlist); // This is what you need to use

        // Set the wishlist state with the nested array
        setWishlist(data.wishlist);
      } catch (error) {
        console.error("Error fetching wishlist:", error);
        setError(error.response?.data?.message || "Error fetching wishlist.");
      } finally {
        setLoading(false);
      }
    };

    fetchWishlist();
  }, []);

  return (
    <div className="dashboard">
      <h2 className="dashboard-title">My Wishlist</h2>

      {loading ? (
        <p>Loading your wishlist...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : wishlist.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <div className="product-grid">
          {wishlist.map((product) => (
            <div className="product-card" key={product._id}>
              <img
                src={product.image || "/default-product.png"}
                alt={product.name || "Product"}
                onError={(e) => {
                  e.target.src = "/default-product.png";
                }}
              />
              <h3>{product.name || "Product Name"}</h3>
              <p className="price">
                {product.price
                  ? `$${product.price.toFixed(2)}`
                  : "Price not available"}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
