import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import "./Dashboard.css";

const ProductDashboard = () => {
  const [products, setProducts] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [cart, setCart] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [token] = useState(localStorage.getItem("token"));

  // Fetch products
  const fetchProducts = async () => {
    try {
      const { data } = await axios.get("http://localhost:5000/api/products");
      setProducts(data);
    } catch (err) {
      console.error("Error fetching products:", err);
    }
  };

  // Fetch wishlist
  const fetchWishlist = async () => {
    if (token) {
      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/wishlist/",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setWishlist(data.wishlist || []);
      } catch (err) {
        console.error("Error fetching wishlist:", err);
      }
    }
  };

  // Toggle wishlist item
  const toggleWishlist = async (productId) => {
    if (token) {
      try {
        if (wishlist.includes(productId)) {
          // Remove from wishlist
          await axios.put(
            "http://localhost:5000/api/wishlist/remove",
            { productId },
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );
          setWishlist((prev) => prev.filter((id) => id !== productId));
        } else {
          // Add to wishlist
          await axios.put(
            "http://localhost:5000/api/wishlist",
            { productId },
            {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );
          setWishlist((prev) => [...prev, productId]);
        }
      } catch (err) {
        console.error("Error updating wishlist:", err);
      }
    } else {
      console.log("Please login to manage your wishlist.");
    }
  };

  // Toggle cart item
  const toggleCart = (productId) => {
    setCart((prev) =>
      prev.includes(productId)
        ? prev.filter((id) => id !== productId)
        : [...prev, productId]
    );
  };

  // Filter products based on search query
  const filteredProducts = products.filter(
    (product) =>
      product.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      product.category.toLowerCase().includes(searchQuery.toLowerCase())
  );

  // Fetch products and wishlist when the token changes
  useEffect(() => {
    fetchProducts();
    fetchWishlist();
  }, [token]);

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="dashboard-header">
        <h1>My Shop</h1>
        <input
          type="text"
          placeholder="Search by name or category..."
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="search-bar"
        />
        <Link to="/wishlist">
          <button className="wishlist-btn">Go to Wishlist</button>
        </Link>
        <Link to="/profile">
          <button className="profile-btn">Profile</button>
        </Link>
      </header>

      {/* Title */}
      <h2 className="dashboard-title">Product Dashboard</h2>

      {/* Product Grid */}
      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div className="product-card" key={product._id}>
            <Link to={`/product/${product._id}`}>
              <img
                src={product.image || "https://via.placeholder.com/300x200"}
                alt={product.name}
              />
              <h3>{product.name}</h3>
              <p className="category">{product.category}</p>
              {/* Format price in INR */}
              <p className="price">
                {product.price.toLocaleString("en-IN", {
                  style: "currency",
                  currency: "INR",
                })}
              </p>
              <div className="details">
                <span>‚≠ê {product.rating}</span>
                <span>Stock: {product.stock}</span>
              </div>
            </Link>
            <div className="actions">
              <label>
                <input
                  type="checkbox"
                  checked={wishlist.includes(product._id)}
                  onChange={() => toggleWishlist(product._id)}
                />{" "}
                Wishlist
              </label>
              <label>
                <input
                  type="checkbox"
                  checked={cart.includes(product._id)}
                  onChange={() => toggleCart(product._id)}
                />{" "}
                Cart
              </label>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductDashboard;
