import React, { useEffect, useState } from "react";
import axios from "axios";
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlist, setWishlist] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchWishlist = async () => {
      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`,
            },
          }
        );

        console.log("Fetched Wishlist Data:", data);

        // Check if data is an array or if it's nested in a user object
        const wishlistData = Array.isArray(data)
          ? data
          : data.wishlist && Array.isArray(data.wishlist)
          ? data.wishlist
          : [];

        setWishlist(wishlistData);
      } catch (error) {
        console.error("Error fetching wishlist:", error);
        setError(error.response?.data?.message || "Error fetching wishlist.");
      } finally {
        setLoading(false);
      }
    };

    fetchWishlist();
  }, []);

  return (
    <div className="dashboard">
      <h2 className="dashboard-title">My Wishlist</h2>

      {loading ? (
        <p>Loading your wishlist...</p>
      ) : error ? (
        <p className="error-message">{error}</p>
      ) : wishlist.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <div className="product-grid">
          {wishlist.map((product) => (
            <div className="product-card" key={product._id}>
              <img
                src={product.image || "/default-product-image.png"}
                alt={product.name}
                onError={(e) => {
                  e.target.src = "/default-product-image.png";
                }}
              />
              <h3>{product.name}</h3>
              <p className="price">${product.price?.toFixed(2)}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Wishlist;
