import React, { useState } from "react";

const BuyNowPage = () => {
  const [orderConfirmed, setOrderConfirmed] = useState(false);

  const handleBuyNow = () => {
    // Simulate order placement (COD only)
    setTimeout(() => {
      setOrderConfirmed(true);
    }, 1000);
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen p-4 bg-gray-100">
      {!orderConfirmed ? (
        <div className="bg-white p-6 rounded-xl shadow-md w-full max-w-md text-center">
          <h2 className="text-2xl font-semibold mb-4">Choose Payment Method</h2>
          <p className="text-gray-600 mb-6">
            Only Cash on Delivery is available at this time.
          </p>
          <button
            onClick={handleBuyNow}
            className="bg-green-600 text-white px-6 py-2 rounded-xl hover:bg-green-700 transition"
          >
            Confirm Order (Cash on Delivery)
          </button>
        </div>
      ) : (
        <div className="bg-white p-6 rounded-xl shadow-md w-full max-w-md text-center">
          <h2 className="text-2xl font-bold text-green-600 mb-4">
            Order Confirmed!
          </h2>
          <p className="text-gray-700">
            Thank you for your order. You chose{" "}
            <strong>Cash on Delivery</strong>. Your order will be delivered
            soon.
          </p>
        </div>
      )}
    </div>
  );
};

export default BuyNowPage;
