import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "./ProductDetail.css";

const ProductDetail = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [error, setError] = useState("");
  const [wishlistLoading, setWishlistLoading] = useState(false);
  const [isInWishlist, setIsInWishlist] = useState(false);

  useEffect(() => {
    const getProduct = async () => {
      try {
        const { data } = await axios.get(
          `http://localhost:5000/api/products/${id}`
        );
        if (data) {
          setProduct(data);
        } else {
          setError("Product not found.");
        }
      } catch (error) {
        console.error("Error fetching product data:", error);
        setError("Failed to load product details. Please try again later.");
      }
    };

    const checkWishlist = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) return;

        const { data } = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (data.wishlist.includes(id)) {
          setIsInWishlist(true);
        }
      } catch (error) {
        console.error("Error checking wishlist:", error);
      }
    };

    getProduct();
    checkWishlist();
  }, [id]);

  const handleAddToWishlist = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("Please log in to add to wishlist.");
        return;
      }

      setWishlistLoading(true);

      await axios.post(
        "http://localhost:5000/api/users/wishlist",
        { productId: id },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );

      setIsInWishlist(true);
      setWishlistLoading(false);
    } catch (error) {
      console.error("Error adding to wishlist:", error);
      alert("Failed to add to wishlist. Please try again.");
      setWishlistLoading(false);
    }
  };

  if (error) return <p>{error}</p>;
  if (!product) return <p>Loading...</p>;

  return (
    <div className="product-detail">
      <div className="product-detail-container">
        <img
          src={product.image || "fallback-image-url.jpg"}
          alt={product.name}
          className="product-image"
        />
        <div className="info">
          <h2 className="product-name">{product.name}</h2>
          <p className="category">Category: {product.category}</p>
          <p className="price">Price: ‚Çπ{product.price.toLocaleString()}</p>
          <p className="description">{product.description}</p>
          <p>
            <strong>Stock Available:</strong> {product.stock}
          </p>
          <p>
            <strong>Brand:</strong> {product.brand}
          </p>
          <p>
            <strong>Rating:</strong> ‚≠ê {product.rating} ({product.numReviews}{" "}
            reviews)
          </p>
          <p>
            <strong>Featured:</strong> {product.featured ? "Yes" : "No"}
          </p>
          <p>
            <strong>Warranty Period:</strong> {product.warrantyPeriod} months
          </p>
          <p>
            <strong>Created At:</strong>{" "}
            {new Date(product.createdAt).toLocaleDateString("en-IN")}
          </p>
          <p>
            <strong>Updated At:</strong>{" "}
            {new Date(product.updatedAt).toLocaleDateString("en-IN")}
          </p>

          {/* Buttons */}
          <div className="buttons">
            <button className="add-to-cart-btn">Add to Cart</button>

            <button
              className="wishlist-btn"
              onClick={handleProductClick}
              disabled={isInWishlist || wishlistLoading}
            >
              {wishlistLoading
                ? "Adding..."
                : isInWishlist
                ? "Added to Wishlist ‚ù§Ô∏è"
                : "Add to Wishlist ü§ç"}
            </button>
          </div>

          <Link to="/dashboard" className="back-button">
            ‚Üê Back to Dashboard
          </Link>
        </div>
      </div>
    </div>
  );
};

export default ProductDetail;
