import React, { useState, useEffect } from "react";
import axios from "axios";

const ProductDashboard = () => {
  const [isAdmin, setIsAdmin] = useState(false);
  const [confirmed, setConfirmed] = useState(false);
  const [loading, setLoading] = useState(true);

  const [formData, setFormData] = useState({
    name: "",
    description: "",
    price: 0,
    category: "",
    stock: 0,
    brand: "",
    image: "",
    rating: 0,
    numReviews: 0,
    featured: false,
    warrantyPeriod: 0,
  });

  const [success, setSuccess] = useState("");
  const [error, setError] = useState("");

  // ✅ Check admin status
  useEffect(() => {
    const checkAdmin = async () => {
      const token = localStorage.getItem("token");
      if (!token) {
        alert("You are not logged in!");
        window.history.back();
        return;
      }

      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/profile",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (data.isAdmin) {
          setIsAdmin(true);
          setConfirmed(true);
        } else {
          const confirmAdmin = window.confirm(
            "You are going to request access as an Admin. Do you agree?"
          );
          if (confirmAdmin) {
            setConfirmed(true);
          } else {
            window.history.back();
          }
        }
      } catch (err) {
        setError("Failed to verify admin status");
        console.error(err);
        window.history.back();
      } finally {
        setLoading(false);
      }
    };

    checkAdmin();
  }, []);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const token = localStorage.getItem("token");

      const response = await axios.post(
        "http://localhost:5000/api/products",
        formData,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      setSuccess("Product added successfully!");
      setError("");
      setFormData({
        name: "",
        description: "",
        price: 0,
        category: "",
        stock: 0,
        brand: "",
        image: "",
        rating: 0,
        numReviews: 0,
        featured: false,
        warrantyPeriod: 0,
      });
    } catch (err) {
      setError(err.response?.data?.message || "Failed to add product");
      setSuccess("");
    }
  };

  if (loading) return <div>Loading...</div>;
  if (!confirmed) return null;

  return (
    <div style={{ maxWidth: "700px", margin: "auto", padding: "2rem" }}>
      <h2 style={{ marginBottom: "1rem" }}>Admin - Add New Product</h2>

      {success && (
        <div style={{ color: "green", marginBottom: "1rem" }}>{success}</div>
      )}
      {error && (
        <div style={{ color: "red", marginBottom: "1rem" }}>{error}</div>
      )}

      <form onSubmit={handleSubmit}>
        {[
          { name: "name", type: "text" },
          { name: "description", type: "text" },
          { name: "price", type: "number" },
          { name: "category", type: "text" },
          { name: "stock", type: "number" },
          { name: "brand", type: "text" },
          { name: "image", type: "text" },
          { name: "rating", type: "number" },
          { name: "numReviews", type: "number" },
          { name: "warrantyPeriod", type: "number" },
        ].map(({ name, type }) => (
          <div key={name} style={{ marginBottom: "1rem" }}>
            <label
              htmlFor={name}
              style={{ display: "block", marginBottom: "0.3rem" }}
            >
              {name.charAt(0).toUpperCase() + name.slice(1)}
            </label>
            <input
              id={name}
              name={name}
              type={type}
              value={formData[name]}
              onChange={handleChange}
              required={[
                "name",
                "description",
                "price",
                "category",
                "brand",
              ].includes(name)}
              style={{ width: "100%", padding: "0.5rem" }}
            />
          </div>
        ))}

        <div style={{ marginBottom: "1rem" }}>
          <label htmlFor="featured">
            <input
              type="checkbox"
              id="featured"
              name="featured"
              checked={formData.featured}
              onChange={handleChange}
              style={{ marginRight: "0.5rem" }}
            />
            Featured
          </label>
        </div>

        <button
          type="submit"
          style={{
            padding: "0.6rem 1.5rem",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          Submit Product
        </button>
      </form>
    </div>
  );
};

export default ProductDashboard;
