import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "./Dashboard.css";

const ProductDashboard = () => {
  const [products, setProducts] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [cart, setCart] = useState([]);
  const [token] = useState(localStorage.getItem("token"));
  const [searchTerm, setSearchTerm] = useState("");
  const [userInfo, setUserInfo] = useState(null);
  const [showOtpModal, setShowOtpModal] = useState(false);
  const [phoneNumber, setPhoneNumber] = useState("");
  const [otp, setOtp] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchAll = async () => {
      try {
        const [productsRes, wishlistRes, userRes] = await Promise.all([
          axios.get("http://localhost:5000/api/products"),
          axios.get("http://localhost:5000/api/users/wishlist", {
            headers: { Authorization: `Bearer ${token}` },
          }),
          axios.get("http://localhost:5000/api/users/me", {
            headers: { Authorization: `Bearer ${token}` },
          }),
        ]);
        setProducts(productsRes.data);
        setWishlist(wishlistRes.data.wishlist || []);
        setUserInfo(userRes.data);
      } catch (err) {
        console.error("Error loading data", err);
      }
    };
    if (token) fetchAll();
  }, [token]);

  const toggleWishlist = async (productId) => {
    try {
      if (wishlist.includes(productId)) {
        await axios.put(
          "http://localhost:5000/api/users/wishlist/remove",
          { productId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setWishlist((prev) => prev.filter((id) => id !== productId));
      } else {
        await axios.put(
          "http://localhost:5000/api/users/wishlist",
          { productId },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setWishlist((prev) => [...prev, productId]);
      }
    } catch (err) {
      console.error("Error updating wishlist:", err);
    }
  };

  const toggleCart = (productId) => {
    setCart((prev) =>
      prev.includes(productId)
        ? prev.filter((id) => id !== productId)
        : [...prev, productId]
    );
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleSendOtp = async () => {
    try {
      await axios.post(
        "http://localhost:5000/api/send-otp",
        { phoneNumber },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setOtpSent(true);
    } catch (err) {
      console.error("Error sending OTP", err);
    }
  };

  const handleVerifyOtp = async () => {
    try {
      const { data } = await axios.post(
        "http://localhost:5000/api/verify-otp",
        { phoneNumber, otp },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setUserInfo(data.user);
      setShowOtpModal(false);
      setOtp("");
      setPhoneNumber("");
      alert("Admin access granted!");
    } catch (err) {
      console.error("Error verifying OTP", err);
      alert("Invalid OTP or failed to verify.");
    }
  };

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h2>Product Dashboard</h2>
        <div className="header-actions">
          {userInfo && (
            <div className="profile">
              <p>👤 {userInfo.name || userInfo.email}</p>
              {userInfo.isAdmin ? (
                <Link to="/admin">
                  <button className="admin-btn">Admin Dashboard</button>
                </Link>
              ) : (
                <button
                  onClick={() => setShowOtpModal(true)}
                  className="admin-btn"
                >
                  Get Admin Access
                </button>
              )}
            </div>
          )}
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </header>

      <div className="search-container">
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <div className="wishlist-button">
        <Link to="/wishlist">
          <button className="wishlist-btn">Go to Wishlist</button>
        </Link>
      </div>

      <div className="product-grid">
        {filteredProducts.map((product) => (
          <div className="product-card" key={product._id}>
            <Link to={`/product/${product._id}`}>
              <img
                src={product.image || "https://via.placeholder.com/300x200"}
                alt={product.name}
              />
              <h3>{product.name}</h3>
              <p className="category">{product.category}</p>
              <p className="price">${product.price}</p>
              <div className="details">
                <span>⭐ {product.rating}</span>
                <span>Stock: {product.stock}</span>
              </div>
              <div className="tags">
                {product.stock <= 5 && (
                  <span className="tag low-stock">Low Stock</span>
                )}
                {product.rating >= 4.5 && (
                  <span className="tag best-seller">Best Seller</span>
                )}
                {product.category === "New" && (
                  <span className="tag new">New</span>
                )}
              </div>
            </Link>
            <div className="actions">
              <label>
                <input
                  type="checkbox"
                  checked={wishlist.includes(product._id)}
                  onChange={() => toggleWishlist(product._id)}
                />{" "}
                Wishlist
              </label>
              <label>
                <input
                  type="checkbox"
                  checked={cart.includes(product._id)}
                  onChange={() => toggleCart(product._id)}
                />{" "}
                Cart
              </label>
            </div>
          </div>
        ))}
      </div>

      {showOtpModal && (
        <div className="otp-modal">
          <div className="otp-modal-content">
            <h3>Admin Verification</h3>
            {!otpSent ? (
              <>
                <input
                  type="text"
                  placeholder="Enter phone number"
                  value={phoneNumber}
                  onChange={(e) => setPhoneNumber(e.target.value)}
                />
                <button onClick={handleSendOtp}>Send OTP</button>
              </>
            ) : (
              <>
                <input
                  type="text"
                  placeholder="Enter OTP"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value)}
                />
                <button onClick={handleVerifyOtp}>Verify OTP</button>
              </>
            )}
            <button onClick={() => setShowOtpModal(false)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDashboard;
