import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // Added for navigation
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlistProducts, setWishlistProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate(); // Hook for navigating to Product Detail page

  useEffect(() => {
    const fetchWishlist = async () => {
      try {
        const token = localStorage.getItem("token");

        // Step 1: Get wishlist product IDs
        const { data } = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        const productIds = data.wishlist || [];

        // Step 2: Fetch each product by ID
        const productDetails = await Promise.all(
          productIds.map(async (productId) => {
            const res = await axios.get(
              `http://localhost:5000/api/products/${productId}`
            );
            return res.data;
          })
        );

        setWishlistProducts(productDetails);
        setLoading(false);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch wishlist");
        setLoading(false);
      }
    };

    fetchWishlist();
  }, []);

  if (loading) return <p>Loading wishlist...</p>;
  if (error) return <p>{error}</p>;

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`); // Navigate to ProductDetail page
  };

  return (
    <div className="wishlist-container">
      <h2>My Wishlist</h2>
      {wishlistProducts.length === 0 ? (
        <p>Your wishlist is empty.</p>
      ) : (
        <ul className="wishlist-items">
          {wishlistProducts.map((item) => (
            <li
              key={item._id}
              className="wishlist-item"
              onClick={() => handleProductClick(item._id)} // Added click handler
              style={{ cursor: "pointer" }} // Ensures it looks clickable
            >
              <img src={item.image} alt={item.name} />
              <div>
                <h4>{item.name}</h4>
                <p>{item.description}</p>
                <p>
                  <strong>Price:</strong> ${item.price}
                </p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Wishlist;
