import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlistProducts, setWishlistProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchWishlist = async () => {
      try {
        setLoading(true);
        setError("");

        const token = localStorage.getItem("token");

        if (!token) {
          setError("You must be logged in to view your wishlist.");
          setLoading(false);
          navigate("/login"); // Redirect to login if not authenticated
          return;
        }

        // Step 1: Fetch wishlist product IDs
        const response = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        console.log("Wishlist response:", response.data); // Debug log

        if (!response.data || !response.data.wishlist) {
          setWishlistProducts([]);
          setLoading(false);
          return;
        }

        const productIds = response.data.wishlist;

        if (productIds.length === 0) {
          setWishlistProducts([]);
          setLoading(false);
          return;
        }

        // Step 2: Fetch product details for all IDs
        const productDetails = await Promise.all(
          productIds.map(async (productId) => {
            try {
              const res = await axios.get(
                `http://localhost:5000/api/products/${productId}`
              );
              return res.data.product || res.data; // Handle different response structures
            } catch (productError) {
              console.error(
                `Failed to fetch product ${productId}`,
                productError
              );
              return null;
            }
          })
        );

        // Filter out null responses
        const validProducts = productDetails.filter(Boolean);

        setWishlistProducts(validProducts);
      } catch (err) {
        console.error(
          "Error fetching wishlist:",
          err.response?.data || err.message
        );
        setError(
          err.response?.data?.message ||
            "Failed to fetch wishlist. Please try again later."
        );
      } finally {
        setLoading(false);
      }
    };

    fetchWishlist();
  }, [navigate]);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  if (loading) return <div className="loading">Loading wishlist...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="wishlist-container">
      <h2>My Wishlist</h2>
      {wishlistProducts.length === 0 ? (
        <p className="empty-wishlist">Your wishlist is empty.</p>
      ) : (
        <ul className="wishlist-items">
          {wishlistProducts.map((item) => (
            <li
              key={item._id}
              className="wishlist-item"
              onClick={() => handleProductClick(item._id)}
            >
              <img
                src={item.image}
                alt={item.name}
                onError={(e) => {
                  e.target.src = "/placeholder-image.jpg"; // Fallback image
                }}
              />
              <div className="wishlist-item-details">
                <h4>{item.name}</h4>
                <p className="description">{item.description}</p>
                <p className="price">
                  <strong>Price:</strong> ${item.price?.toFixed(2)}
                </p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Wishlist;
