import React, { useEffect, useState, useCallback } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "./Wishlist.css";

const Wishlist = () => {
  const [wishlistProducts, setWishlistProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [removing, setRemoving] = useState({});
  const navigate = useNavigate();

  const fetchWishlist = useCallback(async () => {
    try {
      setLoading(true);
      setError("");
      console.log("Fetching wishlist...");

      const token = localStorage.getItem("token");
      console.log("Token retrieved:", token ? "exists" : "not found");

      if (!token) {
        console.log("No token found, redirecting to login");
        setError("You must be logged in to view your wishlist.");
        setLoading(false);
        navigate("/login");
        return;
      }

      const response = await axios.get("http://localhost:5000/api/wishlist/", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      console.log("Wishlist API response:", response.data);

      const productIds = response.data?.wishlist || [];
      console.log("Product IDs in wishlist:", productIds);

      if (!Array.isArray(productIds)) {
        throw new Error("Invalid wishlist data received.");
      }

      if (productIds.length === 0) {
        console.log("Wishlist is empty");
        setWishlistProducts([]);
        setLoading(false);
        return;
      }

      const productDetails = await Promise.all(
        productIds.map(async (productId) => {
          try {
            console.log(`Fetching product details for ${productId}`);
            const res = await axios.get(
              `http://localhost:5000/api/products/${productId}`
            );
            console.log(`Product ${productId} details:`, res.data);
            return { ...(res.data.product || res.data), inWishlist: true };
          } catch (productError) {
            console.error(
              `Failed to fetch product ${productId}:`,
              productError
            );
            return null;
          }
        })
      );

      const validProducts = productDetails.filter(Boolean);
      console.log("Valid products in wishlist:", validProducts);
      setWishlistProducts(validProducts);
    } catch (err) {
      console.error("Error fetching wishlist:", err);
      if (axios.isAxiosError(err)) {
        const errorMessage =
          err.response?.data?.message ||
          err.response?.statusText ||
          "Failed to fetch wishlist. Server error.";
        console.error("Axios error details:", {
          status: err.response?.status,
          data: err.response?.data,
        });
        setError(errorMessage);
      } else {
        setError(
          err.message || "An unexpected error occurred. Please try again."
        );
      }
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  const removeFromWishlist = async (productId) => {
    try {
      console.log(`Attempting to remove product ${productId}`);
      setRemoving((prev) => ({ ...prev, [productId]: true }));
      const token = localStorage.getItem("token");

      if (!token) {
        setError("You must be logged in to remove items.");
        navigate("/login");
        return;
      }

      const response = await axios.put(
        "http://localhost:5000/api/wishlist/",
        { productId, action: "remove" },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      console.log("Remove response:", response.data);

      setWishlistProducts((prev) =>
        prev.filter((item) => item._id !== productId)
      );
    } catch (err) {
      console.error("Error removing from wishlist:", err);
      if (axios.isAxiosError(err)) {
        setError(
          err.response?.data?.message ||
            "Failed to remove item. Please try again."
        );
      } else {
        setError(err.message || "An unexpected error occurred.");
      }
      await fetchWishlist(); // Refresh wishlist
    } finally {
      setRemoving((prev) => ({ ...prev, [productId]: false }));
    }
  };

  useEffect(() => {
    console.log("Wishlist component mounted");
    fetchWishlist();
  }, [fetchWishlist]);

  const handleProductClick = (productId, e) => {
    if (e.target.closest(".remove-btn")) return;
    navigate(`/product/${productId}`);
  };

  if (loading) return <div className="loading">Loading wishlist...</div>;
  if (error)
    return (
      <div className="error-message">
        {error}
        <div>
          <button onClick={fetchWishlist} className="retry-btn">
            Retry
          </button>
          <button onClick={() => navigate("/login")} className="login-btn">
            Login
          </button>
        </div>
      </div>
    );

  return (
    <div className="wishlist-container">
      <h2>My Wishlist</h2>
      {wishlistProducts.length === 0 && !loading ? (
        <div className="empty-wishlist">
          <p>Your wishlist is empty.</p>
          <button onClick={() => navigate("/products")} className="browse-btn">
            Browse Products
          </button>
        </div>
      ) : (
        <ul className="wishlist-items">
          {wishlistProducts.map((item) => (
            <li
              key={item._id}
              className="wishlist-item"
              onClick={(e) => handleProductClick(item._id, e)}
            >
              <img
                src={item.image || "/placeholder-image.jpg"}
                alt={item.name}
                onError={(e) => {
                  e.target.src = "/placeholder-image.jpg";
                }}
              />
              <div className="wishlist-item-details">
                <h4>{item.name}</h4>
                <p className="description">
                  {item.description?.substring(0, 100)}...
                </p>
                <p className="price">
                  <strong>Price:</strong> ${item.price?.toFixed(2)}
                </p>
                <button
                  className="remove-btn"
                  onClick={(e) => {
                    e.stopPropagation();
                    removeFromWishlist(item._id);
                  }}
                  disabled={removing[item._id]}
                >
                  {removing[item._id] ? "Removing..." : "Remove"}
                </button>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Wishlist;
