import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import "./Dashboard.css";

const ProductDashboard = () => {
  const [products, setProducts] = useState([]);
  const [wishlist, setWishlist] = useState([]);
  const [cart, setCart] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const { data } = await axios.get("http://localhost:5000/api/products");
        setProducts(data);
      } catch (err) {
        console.error("Error fetching products:", err);
        setMessage("Failed to load products.");
      }
    };

    const fetchUserWishlist = async () => {
      const token = localStorage.getItem("token");

      if (!token) {
        setMessage("You must be logged in to access your wishlist.");
        navigate("/login"); // Redirect to login
        return;
      }

      try {
        const { data } = await axios.get(
          "http://localhost:5000/api/users/wishlist",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setWishlist(data.wishlist || []);
      } catch (err) {
        console.error("Error fetching wishlist:", err);
        setMessage("Failed to load wishlist.");
      }
    };

    const loadData = async () => {
      setLoading(true);
      await fetchProducts();
      await fetchUserWishlist();
      setLoading(false);
    };

    loadData();
  }, [navigate]);

  const toggleWishlist = async (productId) => {
    const token = localStorage.getItem("token");

    if (!token) {
      setMessage("Please log in to manage your wishlist.");
      navigate("/login");
      return;
    }

    try {
      if (wishlist.includes(productId)) {
        await axios.put(
          "http://localhost:5000/api/users/wishlist/remove",
          { productId },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setWishlist((prev) => prev.filter((id) => id !== productId));
        setMessage("Removed from wishlist.");
      } else {
        await axios.put(
          "http://localhost:5000/api/users/wishlist",
          { productId },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setWishlist((prev) => [...prev, productId]);
        setMessage("Added to wishlist.");
      }
    } catch (err) {
      console.error("Error updating wishlist:", err);
      setMessage("Failed to update wishlist.");
    }
  };

  const toggleCart = (productId) => {
    setCart((prev) =>
      prev.includes(productId)
        ? prev.filter((id) => id !== productId)
        : [...prev, productId]
    );
  };

  return (
    <div className="dashboard">
      <h2 className="dashboard-title">Product Dashboard</h2>

      <div className="wishlist-button">
        <Link to="/wishlist">
          <button className="wishlist-btn">Go to Wishlist</button>
        </Link>
      </div>

      {message && <p className="message">{message}</p>}
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="product-grid">
          {products.map((product) => (
            <div className="product-card" key={product._id}>
              <Link to={`/product/${product._id}`}>
                <img
                  src={product.image || "https://via.placeholder.com/300x200"}
                  alt={product.name}
                />
                <h3>{product.name}</h3>
                <p className="category">{product.category}</p>
                <p className="price">${product.price}</p>
                <div className="details">
                  <span>‚≠ê {product.rating}</span>
                  <span>Stock: {product.stock}</span>
                </div>
              </Link>
              <div className="actions">
                <label>
                  <input
                    type="checkbox"
                    checked={wishlist.includes(product._id)}
                    onChange={() => toggleWishlist(product._id)}
                  />{" "}
                  Wishlist
                </label>
                <label>
                  <input
                    type="checkbox"
                    checked={cart.includes(product._id)}
                    onChange={() => toggleCart(product._id)}
                  />{" "}
                  Cart
                </label>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductDashboard;
