import React from "react";
import { Link } from "react-router-dom";
import {
  FaSearch,
  FaHeart,
  FaShoppingCart,
  FaUser,
  FaPlus,
} from "react-icons/fa";

const Header = ({
  pathname,
  searchQuery,
  setSearchQuery,
  sortOption,
  setSortOption,
  filterCategory,
  setFilterCategory,
  categories,
  wishlist,
  cart,
}) => {
  // Pages to hide search & filter
  const hideSearchAndFilter = ["/profile", "/wishlist", "/AdminProductForm"];
  const isProductPage = pathname.startsWith("/product/");
  const showSearchAndFilter =
    !hideSearchAndFilter.includes(pathname) && !isProductPage;

  // Hide wishlist button only on wishlist page
  const showWishlistButton = pathname !== "/wishlist";

  return (
    <header className="dashboard-header">
      <h1>ðŸ›’ ShopEasy</h1>

      {showSearchAndFilter && (
        <div className="header-controls">
          <input
            type="text"
            placeholder="Search by name or category..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="search-bar"
          />
          <div className="sort-filter-container">
            <select
              value={sortOption}
              onChange={(e) => setSortOption(e.target.value)}
              className="sort-select"
            >
              <option value="default">Default</option>
              <option value="price-asc">Price: Low to High</option>
              <option value="price-desc">Price: High to Low</option>
              <option value="rating">Top Rated</option>
            </select>
            <select
              value={filterCategory}
              onChange={(e) => setFilterCategory(e.target.value)}
              className="filter-select"
            >
              {categories.map((category) => (
                <option key={category} value={category}>
                  {category.charAt(0).toUpperCase() + category.slice(1)}
                </option>
              ))}
            </select>
          </div>
        </div>
      )}

      <div className="header-actions">
        {showWishlistButton && (
          <Link to="/wishlist">
            <button className="wishlist-btn">
              <FaHeart /> Wishlist ({wishlist.length})
            </button>
          </Link>
        )}
        <Link to="/cart">
          <button className="cart-btn">
            <FaShoppingCart /> Cart ({cart.length})
          </button>
        </Link>
        <Link to="/AdminProductForm">
          <button className="wishlist-btn">
            <FaPlus /> Add Product
          </button>
        </Link>
        <Link to="/profile">
          <button className="profile-btn">
            <FaUser /> Profile
          </button>
        </Link>
      </div>
    </header>
  );
};

export default Header;
