import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import "./UserDropdown.css"; // Optional: separate CSS for dropdown

const UserDropdown = ({ userInfo }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  return (
    <div className="profile-dropdown" ref={dropdownRef}>
      <button
        className="profile-btn"
        onClick={() => setShowDropdown((prev) => !prev)}
      >
        ðŸ‘¤ {userInfo?.name || userInfo?.email}
      </button>

      {showDropdown && (
        <div className="dropdown-content">
          <p>
            <strong>Name:</strong> {userInfo?.name || "N/A"}
          </p>
          <p>
            <strong>Email:</strong> {userInfo?.email}
          </p>
          <p>
            <strong>Role:</strong> {userInfo?.role}
          </p>
          <Link to="/profile">
            <button className="profile-details-btn">View Profile</button>
          </Link>
          {userInfo?.role === "admin" && (
            <Link to="/admin">
              <button className="admin-btn">Admin Dashboard</button>
            </Link>
          )}
        </div>
      )}
    </div>
  );
};

export default UserDropdown;
